// Adaptee: classe legada com interface incompatível
public class JpegReader {
    public void readJpeg(String path) {
        System.out.println("Lendo JPEG: " + path);
    }
}

// Adapter: adapta JpegReader para interface Converter
public class JpegToPngAdapter implements Converter {
    private JpegReader jpegReader;

    public JpegToPngAdapter(JpegReader jpegReader) {
        this.jpegReader = jpegReader;
    }

    @Override
    public void convert(String source) {
        jpegReader.readJpeg(source);
        System.out.println("Convertendo para PNG: " + source + ".png");
    }
}

