// Adaptee: biblioteca legada que só lê JPEG e retorna bytes simulados
public class LegacyJPEGReader {
    public byte[] loadJPEG(String path) {
        System.out.println("Lendo JPEG legado: " + path);
        // Simulação de leitura
        return ("JPEG_DATA:" + path).getBytes();
    }
}

