// Target: interface que o cliente espera usar
public interface ImageConverter {
    void convert(String imagePath);
}

public class Main {
    public static void main(String[] args) {
        // Criando o serviço legado
        JpegService service = new JpegService();

        // Criando o adapter que envolve o serviço
        ImageConverter adapter = new JpegAdapter(service);

        // Cliente usa apenas a interface, não conhece JpegService
        Cliente cliente = new Cliente(adapter);

        // Fluxo: Cliente → ImageConverter → Adapter → JpegService
        cliente.converterImagem("foto1.jpg");
        cliente.converterImagem("foto2.jpg");
    }
}

